// Login form handler – talks directly to the external API
// ---------------------------------------------------------------
document.getElementById('loginForm').addEventListener('submit', async function(e){
    e.preventDefault();

    const username = document.getElementById('username').value.trim();
    const password = document.getElementById('password').value.trim();
    const messageDiv = document.getElementById('message');
    const loginBtn   = document.getElementById('loginButton');
    const loading    = document.getElementById('loading');

    // UI feedback
    loading.style.display = 'inline-block';
    loginBtn.disabled = true;
    messageDiv.innerHTML = '';

    try {
        const response = await fetch('https://maxxenergy.duckdns.org:8080/api/login', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ username, password })
        });

        const data = await response.json();

        if (data.success) {
            // Store the JWT in a cookie (1‑day expiry)
            setCookie('authToken', data.token, 1);

            // Optional: store extra info (e.g. user role) if you need it later
            if (data.user && data.user.role) {
                setCookie('userRole', data.user.role, 1);
            }

            messageDiv.innerHTML = '<div class="message success">Login successful! Redirecting…</div>';

            // Redirect after a short pause (give the cookie a moment to settle)
            setTimeout(() => {
                // Change this URL to whatever page you want the user to land on after login
                window.location.href = '/wordpress/?page_id=193';
            }, 1200);
        } else {
            // API returned success:false
            messageDiv.innerHTML = `<div class="message error">${data.message || 'Login failed'}</div>`;
        }
