<script>
(function(){
 document.addEventListener('DOMContentLoaded', () => {
 const host = document.getElementById('co2-raw');
 if(!host) return;

 const parse = (txt)=>{
 try{ return JSON.parse(txt); }catch(_){}
 const m = txt && txt.match(/\{[\s\S]*\}/);
 if(!m) return null;
 try{ return JSON.parse(m[0]); }catch(_){ return null; }
 };

 const rawText = host.textContent.trim();
 const data = parse(rawText);
 if(!data){
 host.style.whiteSpace='pre-wrap';
 host.style.fontFamily='monospace';
 host.style.color='#b91c1c';
 host.textContent = '⚠️ Unable to parse API JSON.\n' + rawText;
 return;
 }

 const fmt = (n, d=0)=> new Intl.NumberFormat().format(Number(n).toFixed(d));
 const toBillions = (n)=> n/1_000_000_000;

 const coalVal = Number(data.coal_co2_saved || 0);
 const gasVal = Number(data.natural_gas_co2_saved || 0);

 const html = `
 <div class="co2-card" role="region" aria-label="CO₂ Avoided">
 <h3 class="glossy">🌍 CO₂ Avoided</h3>
 <div class="co2-grid">
 <div class="co2-tile coal-tile">
 <div class="co2-label glossy">Coal</div>
 <div class="divider"></div>
 <div class="co2-value glossy">${fmt(coalVal)} g/kWh</div>
 <div class="co2-sub glossy">${fmt(toBillions(coalVal),3)} billion g/kWh</div>
 </div>
 <div class="co2-tile gas-tile">
 <div class="co2-label glossy">Natural Gas</div>
 <div class="divider"></div>
 <div class="co2-value glossy">${fmt(gasVal)} g/kWh</div>
 <div class="co2-sub glossy">${fmt(toBillions(gasVal),3)} billion g/kWh</div>
 </div>
 </div>
 </div>
 `;

 host.outerHTML = html;
 });
})();
