<script>
(function(){
 document.addEventListener('DOMContentLoaded', () => {
 const host = document.getElementById('gen-raw');
 if(!host) return;

 const parse = (txt)=>{
 try{ return JSON.parse(txt); }catch(_){}
 const m = txt && txt.match(/\{[\s\S]*\}/);
 if(!m) return null;
 try{ return JSON.parse(m[0]); }catch(_){ return null; }
 };

 const rawText = host.textContent.trim();
 const data = parse(rawText);
 if(!data || typeof data !== 'object'){
 host.style.whiteSpace='pre-wrap';
 host.style.fontFamily='monospace';
 host.style.color='#b91c1c';
 host.textContent = '⚠️ Unable to parse annual_generation JSON.\n' + rawText;
 return;
 }

 const fmt = (n, d=0)=> String(Number(n).toFixed(d)); // plain numbers, no commas

 const entries = Object.entries(data);
 const nicify = (k)=> k.replace(/_/g,' ')
 .replace(/\s+/g,' ')
 .trim()
 .replace(/\b\w/g, m => m.toUpperCase());

 const tiles = entries.map(([key,val], idx) => {
 const num = Number(val || 0);
 const accent = `accent-${idx % 4}`;
 const label = nicify(key);

 return `
 <div class="gen-tile ${accent}">
 <div class="gen-label glossy">${label}</div>
 <div class="gen-divider"></div>
 <div class="gen-value glossy">${fmt(num)}</div>
 </div>
 `;
 }).join('');

 const html = `
 <div class="gen-card" role="region" aria-label="Annual Generation">
 <h3 class="glossy">⚡ Annual Generation</h3>
 <div class="gen-grid">
 ${tiles}
 </div>
 </div>
 `;

 host.outerHTML = html;
 });
})();
</script>
				</div>
				</div>
