<script>
(function(){
 document.addEventListener('DOMContentLoaded', () => {
 const host = document.getElementById('cap-raw');
 if(!host) return;

 const UNIT = 'MW';

 const parse = (txt)=>{
 const ta = document.createElement('textarea'); // decode entities
 ta.innerHTML = txt;
 const decoded = ta.value;
 const stripped = decoded.replace(/<[^>]+>/g,''); // strip tags
 const m = stripped.match(/\{[\s\S]*\}/);
 if(!m) return null;
 try{ return JSON.parse(m[0]); }catch(_){ return null; }
 };

 const rawText = host.textContent.trim();
 const data = parse(rawText);
 if(!data){
 host.style.whiteSpace='pre-wrap';
 host.style.fontFamily='monospace';
 host.style.color='#b91c1c';
 host.textContent = '⚠️ Unable to parse total_capacity JSON.\n' + rawText;
 return;
 }

 const fmt = (n, d=0)=> new Intl.NumberFormat().format(Number(n).toFixed(d));
 const nicify = (k)=> k.replace(/_/g,' ').replace(/\s+/g,' ')
 .trim().replace(/\b\w/g, m => m.toUpperCase());

 const [key, val] = Object.entries(data)[0];
 const num = Number(val || 0);

 const html = `
 <div class="cap-card" role="region" aria-label="Total Capacity">
 <h3 class="glossy">🏗️ Total Capacity</h3>
 <div class="cap-grid">
 <div class="cap-tile">
 <div class="cap-label glossy">${nicify(key)}</div>
 <div class="cap-divider"></div>
 <div class="cap-value glossy">${fmt(num)} ${UNIT}</div>
 </div>
 </div>
 </div>
 `;

 host.outerHTML = html;
 });
})();
</script>
