<script>
(function(){
  // Guard against double init
  if (window.__UPTIME_CARD_INIT__) {
    // If something re-initializes, remove duplicates then proceed anyway
    document.querySelectorAll('.uptime-card').forEach(n => n.remove());
  }
  window.__UPTIME_CARD_INIT__ = true;

  const $ = (sel, root=document) => root.querySelector(sel);

  // Parse JSON even if surrounded by other text
  const safeParse = (txt) => {
    if(!txt) return null;
    try { return JSON.parse(txt.trim()); } catch(_){}
    const m = txt.match(/\{[\s\S]*\}/);
    if(m){ try { return JSON.parse(m[0]); } catch(_){ } }
    return null;
  };

  // Wait for shortcode JSON (wpgetapi may render late)
  const waitForJson = (selector, timeoutMs=6000) => new Promise((resolve, reject) => {
    const start = performance.now();
    (function tick(){
      const el = $(selector);
      if(el){
        const txt = el.textContent || el.innerText || '';
        const data = safeParse(txt);
        if(data){ resolve({el, data}); return; }
      }
      if(performance.now() - start > timeoutMs){ reject(new Error('timeout')); return; }
      requestAnimationFrame(tick);
    })();
  });

  const fmtHms = (h,m,s) => `${h}h ${m}m ${s}s`;
  const hmsFromSeconds = (totalSec) => {
    const h = Math.floor(totalSec / 3600);
    const m = Math.floor((totalSec % 3600) / 60);
    const s = Math.floor(totalSec % 60);
    return {h, m, s};
  };

  document.addEventListener('DOMContentLoaded', async () => {
    try {
      const {el: host, data} = await waitForJson('#uptime-raw');

      // Take hours/minutes/seconds from API (fallback 0)
      const baseH = Math.max(0, Number(data.hours || 0));
      const baseM = Math.max(0, Number(data.minutes || 0));
      const baseS = Math.max(0, Number(data.seconds || 0));
      const initialTotal = baseH*3600 + baseM*60 + baseS;

      // Remove ANY existing uptime cards before inserting (prevents duplicates)
      document.querySelectorAll('.uptime-card').forEach(n => n.remove());

      // Build single card (no divider, no accent bar)
      const card = document.createElement('div');
      card.id = 'uptime-card';
      card.className = 'uptime-card';
      card.setAttribute('role','region');
      card.setAttribute('aria-label','Uptime');
      card.innerHTML = `
        <h3>✅ Uptime</h3>
        <div class="uptime-tile">
          <div class="uptime-label">UPTIME</div>
          <div class="uptime-value" id="uptime-value">0h 0m 0s</div>
        </div>
      `;

      // Insert before placeholder and remove the placeholder (and its content) so it never shows
      if (host && host.parentNode) {
        host.parentNode.insertBefore(card, host);
        host.remove();
      } else {
        document.body.appendChild(card);
      }

      const valueEl = document.getElementById('uptime-value');

      // Drift-free live update
      const startTs = Date.now();
      let lastShown = -1;
      const render = () => {
        const elapsed = Math.floor((Date.now() - startTs) / 1000);
        const total = initialTotal + elapsed;
        if (total !== lastShown) {
          lastShown = total;
          const {h, m, s} = hmsFromSeconds(total);
          valueEl.textContent = fmtHms(h, m, s);
        }
        requestAnimationFrame(render);
      };
      render();

    } catch (e) {
      const host = document.getElementById('uptime-raw');
      if(host){
        host.insertAdjacentHTML('afterend',
          '<div class="uptime-error">⚠️ Could not render Uptime card (no JSON detected in #uptime-raw in time).</div>');
      }
      console.error('Uptime render error:', e);
    }
  });
