    <script>
        document.getElementById('loginForm').addEventListener('submit', async function(e) {
            e.preventDefault();

            const username = document.getElementById('username').value;
            const password = document.getElementById('password').value;
            const messageDiv = document.getElementById('message');
            const loginButton = document.getElementById('loginButton');
            const loading = document.getElementById('loading');

            // Show loading spinner
            loading.style.display = 'inline-block';
            loginButton.disabled = true;
            messageDiv.innerHTML = '';

            try {
                // Update this URL to match your API endpoint
                const response = await fetch('https://maxxenergy.duckdns.org:8080/api/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ username, password })
                });

                const data = await response.json();

                if (data.success) {
                    messageDiv.innerHTML = '<div class="message success">Login successful! Redirecting...</div>';

                    // Store authentication token
                    localStorage.setItem('authToken', data.token);

                    // Redirect after success
                    setTimeout(() => {
                        window.location.href = '/wordpress/?page_id=193';
                    }, 1000);
                } else {
                    messageDiv.innerHTML = '<div class="message error">' + (data.message || 'Login failed') + '</div>';
                }
            } catch (error) {
                messageDiv.innerHTML = '<div class="message error">Login failed: ' + error.message + '</div>';
            } finally {
                // Hide loading spinner
                loading.style.display = 'none';
                loginButton.disabled = false;
            }
        });
    </script>"},"elements":[],"widgetType":"html"}],"isInner":false},{"id":"3e111fc","elType":"container","settings":{"flex_direction":"column"},"elements":[{"id":"408815e","elType":"widget","settings":{"image":{"url":"http://45.55.44.12/wordpress/wp-content/uploads/2025/09/MAXX-Energy-Logo-1B.png","id":236,"size":"","alt":"","source":"library"},"image_size":"thumbnail"},"elements":[],"widgetType":"image"}],"isInner":false}]

